import React, { useState } from "react";
import axios from "./API";

const App = () => {
    const [messages, setMessages] = useState([]);
    const [userInput, setUserInput] = useState("");

    const handleSend = async () => {
        if (!userInput.trim()) return;

        // Add user's message to the chat
        setMessages((prev) => [...prev, { role: "user", content: userInput }]);
        const userMessage = userInput;
        setUserInput("");

        try {
            // Send user input to the backend
            const { data } = await axios.post("/chat", { user_input: userMessage });

            // Add bot's response to the chat
            const botMessages = Array.isArray(data.response)
                ? data.response.map((url, idx) => ({
                      role: "assistant",
                      content: <img key={idx} src={url} alt="Cat" className="rounded-lg shadow-md max-w-full mx-auto" />,
                  }))
                : [{ role: "assistant", content: data.response }];

            setMessages((prev) => [...prev, ...botMessages]);
        } catch (error) {
            setMessages((prev) => [
                ...prev,
                { role: "assistant", content: "Oops! Something went wrong. Please try again." },
            ]);
            console.error(error);
        }
    };

    return (
        <div className="flex flex-col h-screen bg-gray-100">
            <header className="bg-gray-900 text-white text-center py-3">
                <h1 className="text-lg font-semibold">CatGPT</h1>
            </header>
            <main className="flex-1 overflow-y-auto p-4 space-y-4">
                <div className="flex flex-col space-y-4">
                    {messages.map((msg, index) => (
                        <div
                            key={index}
                            className={`flex ${
                                msg.role === "user" ? "justify-end" : "justify-start"
                            }`}
                        >
                            <div
                                className={`${
                                    msg.role === "user"
                                        ? "bg-blue-500 text-white"
                                        : "bg-gray-200 text-gray-800"
                                } p-3 rounded-lg max-w-xs shadow`}
                            >
                                {typeof msg.content === "string" ? (
                                    msg.content
                                ) : (
                                    msg.content
                                )}
                            </div>
                        </div>
                    ))}
                </div>
            </main>
            <footer className="bg-white p-4 flex items-center gap-2">
                <input
                    type="text"
                    value={userInput}
                    onChange={(e) => setUserInput(e.target.value)}
                    placeholder="Type your message..."
                    className="flex-1 border border-gray-300 rounded-lg p-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
                <button
                    onClick={handleSend}
                    className="bg-blue-500 text-white px-4 py-2 rounded-lg hover:bg-blue-600"
                >
                    Send
                </button>
            </footer>
        </div>
    );
};

export default App;
