import React, { useState, useRef, useEffect } from "react";
import axios from "./API";

const App = () => {
    const [messages, setMessages] = useState([]);
    const [userInput, setUserInput] = useState("");
    const [modalImage, setModalImage] = useState(null); // For displaying the enlarged image
    const chatContainerRef = useRef(null);

    const handleSend = async () => {
        if (!userInput.trim()) return;

        setMessages((prev) => [...prev, { role: "user", content: userInput }]);
        const userMessage = userInput;
        setUserInput("");

        try {
            const { data } = await axios.post("/chat", { user_input: userMessage });

            const botMessages = Array.isArray(data.response)
                ? [
                      {
                          role: "assistant",
                          content: (
                              <div className="image-group">
                                  {data.response.map((url, idx) => (
                                      <img
                                          key={idx}
                                          src={url}
                                          alt={`Image ${idx + 1}`}
                                          className="image-thumbnail"
                                          onClick={() => setModalImage(url)} // Show modal on click
                                      />
                                  ))}
                              </div>
                          ),
                      },
                  ]
                : [{ role: "assistant", content: data.response }];

            setMessages((prev) => [...prev, ...botMessages]);
        } catch (error) {
            setMessages((prev) => [
                ...prev,
                { role: "assistant", content: "Oops! Something went wrong. Please try again." },
            ]);
            console.error(error);
        }
    };

    useEffect(() => {
        if (chatContainerRef.current) {
            chatContainerRef.current.scrollTop = chatContainerRef.current.scrollHeight;
        }
    }, [messages]);

    return (
        <div className="chat-container">
            <header className="chat-header">CatGPT</header>
            <main ref={chatContainerRef} className="chat-body">
                {messages.map((msg, index) => (
                    <div
                        key={index}
                        className={`chat-message ${
                            msg.role === "user" ? "user-message" : "bot-message"
                        }`}
                    >
                        {typeof msg.content === "string" ? (
                            <p>{msg.content}</p>
                        ) : (
                            msg.content
                        )}
                    </div>
                ))}
            </main>
            <footer className="chat-footer">
                <input
                    type="text"
                    value={userInput}
                    onChange={(e) => setUserInput(e.target.value)}
                    placeholder="Type a message..."
                    onKeyPress={(e) => {
                        if (e.key === "Enter") {
                            handleSend();
                        }
                    }}
                    className="chat-input"
                />
                <button onClick={handleSend} className="chat-send-button">
                    Send
                </button>
            </footer>

            {modalImage && (
                <div className="modal" onClick={() => setModalImage(null)}>
                    <img src={modalImage} alt="Enlarged" className="modal-image" />
                </div>
            )}
        </div>
    );
};

export default App;
