import React, { useState, useEffect, useRef } from "react";
import axios from "./API";

const App = () => {
    const [messages, setMessages] = useState([]);
    const [userInput, setUserInput] = useState("");
    const [enlargedImage, setEnlargedImage] = useState(null); // For modal
    const chatContainerRef = useRef(null);
    const [headerText, setHeaderText] = useState(""); // Typing effect state

    // Typing effect for the header
    useEffect(() => {
        const fullText = "CatGPT";
        let index = 0;
        setHeaderText(""); // Reset header text
        const interval = setInterval(() => {
            setHeaderText((prev) => prev + fullText[index]);
            index++;
            if (index === fullText.length) clearInterval(interval);
        }, 300); // Adjust speed as desired
        return () => clearInterval(interval); // Cleanup on unmount
    }, []);

    const handleSend = async () => {
        if (!userInput.trim()) return;

        setMessages((prev) => [...prev, { role: "user", content: userInput }]);
        const userMessage = userInput;
        setUserInput("");

        try {
            const { data } = await axios.post("/chat", { user_input: userMessage });

            const botMessages = Array.isArray(data.response)
                ? [
                      {
                          role: "assistant",
                          content: data.response.map((url, idx) => (
                              <img
                                  key={idx}
                                  src={url}
                                  alt={`Image ${idx}`}
                                  className="image-thumbnail"
                                  onClick={() => setEnlargedImage(url)} // Open modal
                              />
                          )),
                      },
                  ]
                : [{ role: "assistant", content: data.response }];

            setMessages((prev) => [...prev, ...botMessages]);
        } catch (error) {
            setMessages((prev) => [
                ...prev,
                { role: "assistant", content: "Oops! Something went wrong. Please try again." },
            ]);
            console.error(error);
        }
    };

    useEffect(() => {
        if (chatContainerRef.current) {
            chatContainerRef.current.scrollTop = chatContainerRef.current.scrollHeight;
        }
    }, [messages]);

    return (
        <div className="chat-container">
            <header className="chat-header">{headerText}</header>
            <main ref={chatContainerRef} className="chat-body">
                {messages.map((msg, index) => (
                    <div
                        key={index}
                        className={`chat-message ${
                            msg.role === "user" ? "user-message" : "bot-message"
                        }`}
                    >
                        {Array.isArray(msg.content) ? (
                            <div className="image-container">{msg.content}</div>
                        ) : (
                            <p>{msg.content}</p>
                        )}
                    </div>
                ))}
            </main>
            <footer className="chat-footer">
                <input
                    type="text"
                    value={userInput}
                    onChange={(e) => setUserInput(e.target.value)}
                    placeholder="Type a message..."
                    onKeyPress={(e) => {
                        if (e.key === "Enter") {
                            handleSend();
                        }
                    }}
                    className="chat-input"
                />
                <button onClick={handleSend} className="chat-send-button">
                    Send
                </button>
            </footer>

            {/* Modal for enlarged image */}
            {enlargedImage && (
                <div className="image-modal" onClick={() => setEnlargedImage(null)}>
                    <div className="modal-content">
                        <img src={enlargedImage} alt="Enlarged view" className="image-full" />
                    </div>
                </div>
            )}
        </div>
    );
};

export default App;
