import React, { useState, useRef, useEffect } from "react";
import axios from "./API";

const App = () => {
    const [messages, setMessages] = useState([]);
    const [userInput, setUserInput] = useState("");
    const chatContainerRef = useRef(null);

    const handleSend = async () => {
        if (!userInput.trim()) return;

        setMessages((prev) => [...prev, { role: "user", content: userInput }]);
        const userMessage = userInput;
        setUserInput("");

        try {
            const { data } = await axios.post("/chat", { user_input: userMessage });

            const botMessages = Array.isArray(data.response)
                ? data.response.map((url, idx) => ({
                      role: "assistant",
                      content: (
                          <img
                              key={idx}
                              src={url}
                              alt="Cat"
                              className="max-w-[150px] max-h-[150px] rounded-lg shadow"
                          />
                      ),
                  }))
                : [{ role: "assistant", content: data.response }];

            setMessages((prev) => [...prev, ...botMessages]);
        } catch (error) {
            setMessages((prev) => [
                ...prev,
                { role: "assistant", content: "Oops! Something went wrong. Please try again." },
            ]);
            console.error(error);
        }
    };

    useEffect(() => {
        if (chatContainerRef.current) {
            chatContainerRef.current.scrollTop = chatContainerRef.current.scrollHeight;
        }
    }, [messages]);

    return (
        <div className="flex flex-col h-screen bg-gray-100">
            <header className="bg-gradient-to-r from-blue-500 to-blue-700 text-white text-center py-3">
                <h1 className="text-lg font-semibold">CatGPT</h1>
            </header>
            <main
                ref={chatContainerRef}
                className="flex-1 overflow-y-auto p-4 space-y-4 bg-white shadow-inner"
            >
                {messages.map((msg, index) => (
                    <div
                        key={index}
                        className={`flex ${
                            msg.role === "user" ? "justify-end" : "justify-start"
                        }`}
                    >
                        <div
                            className={`${
                                msg.role === "user"
                                    ? "bg-blue-500 text-white"
                                    : "bg-gray-200 text-gray-800"
                            } p-3 rounded-xl max-w-[70%] shadow-md`}
                        >
                            {typeof msg.content === "string" ? (
                                <p className="break-words">{msg.content}</p>
                            ) : (
                                msg.content
                            )}
                        </div>
                    </div>
                ))}
            </main>
            <footer className="bg-gray-100 p-4 flex items-center gap-3 border-t">
                <input
                    type="text"
                    value={userInput}
                    onChange={(e) => setUserInput(e.target.value)}
                    placeholder="Type a message..."
                    className="flex-1 border border-gray-300 rounded-full p-3 px-4 focus:outline-none focus:ring-2 focus:ring-blue-500"
                    onKeyPress={(e) => {
                        if (e.key === "Enter") {
                            handleSend();
                        }
                    }}
                />
                <button
                    onClick={handleSend}
                    className="bg-blue-500 text-white px-6 py-3 rounded-full hover:bg-blue-600 shadow-md"
                >
                    Send
                </button>
            </footer>
        </div>
    );
};

export default App;
