import React, { useState } from "react";
import axios from "./API";

const App = () => {
    const [messages, setMessages] = useState([]);
    const [userInput, setUserInput] = useState("");

    const handleSend = async () => {
        if (!userInput.trim()) return;

        // Add user's message to the chat
        setMessages((prev) => [...prev, { role: "user", content: userInput }]);
        const userMessage = userInput;
        setUserInput("");

        try {
            // Send user input to the backend
            const { data } = await axios.post("/chat", { user_input: userMessage });

            // Add bot's response to the chat
            const botMessages = Array.isArray(data.response)
                ? data.response.map((url, idx) => ({
                      role: "assistant",
                      content: <img key={idx} src={url} alt="Cat" style={{ maxWidth: "100%" }} />,
                  }))
                : [{ role: "assistant", content: data.response }];

            setMessages((prev) => [...prev, ...botMessages]);
        } catch (error) {
            setMessages((prev) => [
                ...prev,
                { role: "assistant", content: "Oops! Something went wrong. Please try again." },
            ]);
            console.error(error);
        }
    };

    return (
        <div className="chat-container" style={{ maxWidth: "600px", margin: "20px auto" }}>
            <div className="chat-box" style={{ border: "1px solid #ccc", padding: "10px", height: "400px", overflowY: "scroll" }}>
                {messages.map((msg, index) => (
                    <div
                        key={index}
                        style={{
                            margin: "10px 0",
                            textAlign: msg.role === "user" ? "right" : "left",
                        }}
                    >
                        {msg.content}
                    </div>
                ))}
            </div>
            <div style={{ marginTop: "10px" }}>
                <input
                    type="text"
                    value={userInput}
                    onChange={(e) => setUserInput(e.target.value)}
                    placeholder="Type your message..."
                    style={{
                        width: "calc(100% - 60px)",
                        padding: "10px",
                        marginRight: "10px",
                    }}
                />
                <button onClick={handleSend} style={{ padding: "10px 20px" }}>
                    Send
                </button>
            </div>
        </div>
    );
};

export default App;
